var browser={versions:function(){var u=navigator.userAgent,app=navigator.appVersion;return{isWeixin:!!u.match(/MicroMessenger/i),mobile:!!u.match(/AppleWebKit.*Mobile.*/),ios:!!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/),android:u.indexOf("Android")>-1||u.indexOf("Linux")>-1,iPhone:u.indexOf("iPhone")>-1,iPad:u.indexOf("iPad")>-1}}(),language:(navigator.browserLanguage||navigator.language).toLowerCase()};function mp3_play(mp3_url){if(mp3_url){$("#playbox audio").get(0).pause();$("#playbox audio").get(0).load();$("#playbox audio").get(0).play()}else{if($("#playbox").hasClass("on")){$("#playbox").attr("class","");$("#playbox audio").get(0).pause()}else{$("#playbox").attr("class","on");$("#playbox audio").get(0).play()}}}$(function(){$(".ul_nav li").on("click",function(e){$(".ul_nav li").removeClass("active");$(this).addClass("active")});if(location.hash){var hashArray=location.hash.split("#");pageHash=hashArray[1];var $obj=$(".ul_nav li."+pageHash);if($obj.length){$(".ul_nav li").removeClass("active");$obj.addClass("active")}}var first=0;if(browser.versions.mobile){$("body").on("click tap touchstart swipeUp",function(){if(first==0){$("#playbox audio").get(0).play();first++}})}});
var Utils={alert:{html:function(msg,type){var type=type?type:"success";return'<div class="alert alert-'+type+' alert-dismissible fade in" role="alert">'+'<button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>'+msg+"</div>"}},formVerifyStatus:{add:function(parent,source,type,helpMsg){type=type?type:"success";var statusClass="has-"+type;var feedbackClass="has-feedback";var $parent=parent?$(parent):$(source).parent();$parent.removeClass("has-success has-error has-warning has-feedback").addClass(statusClass).addClass(feedbackClass);var glyphicon="ok";switch(type){case"warning":glyphicon="warning-sign";break;case"error":glyphicon="remove";break}if(helpMsg){if($(".verify-help",$parent).length){$(".verify-help",$parent).html(helpMsg);$(".verify-help",$parent).show()}else{$(source).after('<span class="help-block verify-help">'+helpMsg+"</span>")}}else{$(".verify-help",$parent).hide()}if($(".form-control-feedback",$parent).length){$(".form-control-feedback",$parent).removeClass().addClass("glyphicon glyphicon-"+glyphicon+" form-control-feedback")}else{$(source).after('<span class="glyphicon glyphicon-'+glyphicon+' form-control-feedback"></span>')}},remove:function(parent,source){var $parent=parent?$(parent):$(source).parent();$parent.removeClass("has-success has-warning has-error has-feedback");$(".verify-help",$parent).remove();$(".form-control-feedback",$parent).remove()}}};
$(function(){$("form").submit(function(e){e.preventDefault();var $this=$(this);if($this.data("posting")){return}$("form input, form textarea, form select").trigger("change");if($("form .has-error").length){return}else{$("form .alert").remove()}$this.data("posting",true);$.ajax({"type":"POST","url":$this.attr("action"),"data":$this.serialize(),"dataType":"json","success":function(resp){if(!resp.success){if(resp.data.length==0){$("form .form-group").first().before(Utils.alert.html(resp.message,"warning"))}else{$.each(resp.data,function(i,errorMsg){var $item=$("#"+i);var $parent=$item.closest("div");Utils.formVerifyStatus.add($parent[0],$item[0],"error",errorMsg)})}return}if(resp.data["id"]){addBlessMessage(resp.data,false)}resetForm();$(".alertModal .modal-body").html(resp.message);$(".alertModal").modal("show");$("ul#msglist li").length>0&&$(".no-bless").addClass("hidden")},"complete":function(xhr,textStatus){$this.data("posting",false)}});return false});loadBless();$(".see-more-bless").on("click",function(e){if(!$(this).data("noMore")){loadBless()}});$("form input#userName").on("change",function(){var userName=$.trim($(this).val());if(userName.length){Utils.formVerifyStatus.add(false,this,"success")}else{Utils.formVerifyStatus.add(false,this,"error","请填写您的姓名。");$(this).focus()}});$("form select").on("change",function(){var value=$(this).val();if(value){Utils.formVerifyStatus.remove(false,this)}})});function resetForm(){var $form=$("form");$("form input, form textarea").each(function(){$(this).val("");Utils.formVerifyStatus.remove(false,this)});$("form select").each(function(){$("option",$(this)).first().attr("selected",true);Utils.formVerifyStatus.remove(false,this)})}function loadBless(){var params={max_bless_id:0};var last_card=$("ul#msglist section.bless-card").last();if(last_card.length){params.max_bless_id=last_card.data("id")}$.getJSON("blesses.php",params,function(resp){if(!resp.success||!resp.has_more){$(".see-more-bless").text("没有啦！");$(".see-more-bless").data("noMore",true);
$("ul#msglist li").length>0&&$(".see-more-bless").removeClass("hidden");$("ul#msglist li").length>0&&$(".no-bless").addClass("hidden")}if(!resp.success){return}$.each(resp.data,function(i,item){addBlessMessage(item,true)});$("ul#msglist li").length==0&&$(".no-bless").removeClass("hidden");$("ul#msglist li").length>0&&$(".see-more-bless").removeClass("hidden")})}function addBlessMessage(item,append){append=append?true:false;var item_html=$("#template").html();item_html=item_html.replace("{{bless_id}}",item.id);item_html=item_html.replace("{{created_at}}",item.created_at);item_html=item_html.replace("{{bless_msg}}",item.message);item_html=item_html.replace("{{username}}",item.name);if(append){$("ul#msglist").append(item_html)}else{$("ul#msglist").prepend(item_html)}};
